<style lang="scss">
$subjectColor: skyblue;

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

li {
    list-style: none;
}
html,
body {
    width: 100%;
    // height: 100%;
    overflow-x: hidden;
}

.container {
    // 公共头部区域
    width: 100%;
    height: 100%;
    overflow-x: hidden;
    // position: relative;
    padding: 50px 0;

    .tabs {
        width: 250px;
        height: 100%;
        position: fixed;
        top: 0;
        left: 0;
        background: #fff;

        padding-top: 20px;
        text-align: center;
        img {
            width: 120px;
            height: 120px;
            border-radius: 50%;
        }

        p {
            padding-top: 5px;
            line-height: 50px;
            font-size: 18px;
            font-weight: 700;
        }

        .tabs-icon {
            position: absolute;
            top: 15px;
            left: 20px;
            width: 20px;
            height: 20px;
        }

        ul {
            .tabActive {
                background: $subjectColor;
            }
            li {
                // padding: 10px;
                width: 100%;
                height: 50px;
                line-height: 50px;
                overflow: hidden;

                position: relative;

                a {
                    float: left;
                    width: 100%;
                    font-size: 18px;
                    height: 100%;
                    color: #000;
                    text-decoration: none;
                }
            }
        }

        .tab-bottom {
            width: 100%;
            height: 50px;
            position: absolute;
            bottom: 10px;
            display: flex;
            justify-content: center;
            a{
                display: block;
                width: 40px;
                height: 40px;
                margin-right: 20px;
            }
            .tabs-icon {
                width: 100%;
                height: 100%;
                position:static;
            }
        }
    }

    .broadside-enter,
    .broadside-leave-to {
        transform: translateX(-200px);
    }
    .broadside-enter-active,
    .broadside-leave-active {
        transition: all 0.5s;
    }
}
</style>

<script>
export default {
    data() {
        return {
            wife: this.$store.state.wife,
            tabs: false,
            tabActive: "/home"
        };
    },
    watch: {
        $route(to) {
            this.tabActive = to.path;
            console.log(to.path); //当前路由
        }
    },
    methods: {
        showBroadside() {
            this.tabs = !this.tabs;
        },
        nowClick() {
            let _this = this;
            document.addEventListener("click", event => {
                // tabs是否显示
                let clickNow = event.target.className;
                if (clickNow == "setting" || clickNow == "tabs") {
                    _this.tabs = true;
                } else if (clickNow.baseVal) {
                    // 字体图标的判断方式
                    if (clickNow.baseVal.match("icon-setting")) {
                        _this.tabs = true;
                    }
                } else {
                    _this.tabs = false;
                }
            });
        }
    },
    created() {
        this.nowClick();
    }
};
</script>